import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import axios from "axios";
const Profile = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  let [productData, setProductData] = useState(null);
  useEffect(() => {
    ProductService.get()
      .then((data) => {
        if (Array.isArray(data.data)) {
          setProductData(data.data);
        } else {
          console.log("找不到商品");
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const handleDelete = () => {
    AuthService.deleteUser(currentUser.user._id)
      .then((response) => {
        alert(response.data.message);
        setCurrentUser(null);
        navigate("/");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      {!currentUser && <div>請先登入</div>}
      {currentUser && (
        <div>
          <div>{currentUser.user.username} </div>
          {productData && <p>刊登商品 </p>}
          {!productData && <p>無刊登商品 </p>}
          {/* {productData &&
            productData.map((product) => {
              return (
                <div
                  key={product._id}
                  onClick={() => handleProductClick(product._id)}
                >
                  <img
                    src={product.imageUrl[0]}
                    alt="圖片"
                    style={{ width: "200px", height: "auto" }}
                  />
                  <div>{product.name}</div>
                  <div>{product.quantity}</div>
                  <div>{product.price}</div>
                </div>

              );
            })} */}
          <div className="container">
            <div className=" g-1 row row-cols-xl-4  row-cols-lg-4 row-cols-md-3 ">
              {productData &&
                productData.map((product) => {
                  return (
                    <div
                      className="col"
                      key={product._id}
                      onClick={() => handleProductClick(product._id)}
                    >
                      <div className="productImg">
                        <img
                          className="img-soldout"
                          src={product.imageUrl[0]}
                          alt="圖片"
                          style={{ width: "200px", height: "240px" }}
                        />
                        <div className="soldout-item-content">
                          <div>售完</div>
                        </div>
                      </div>
                      <div>{product.name}</div>
                      <div>{product.quantity}</div>
                      <div>{product.price}</div>
                    </div>
                  );
                })}
            </div>
          </div>
        </div>
      )}
      <button onClick={handleDelete}>刪除帳號</button>
    </div>
  );
};

export default Profile;
