import React, { useState } from "react";
import AuthService from "../services/auth-service";
import { useNavigate } from "react-router-dom";
const Register = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState("");
  const [account, setAccount] = useState("");
  const [password, setPassword] = useState("");
  let [message, setMessage] = useState("");
  const handleUserName = (e) => {
    setUserName(e.target.value);
  };
  const handleAccount = (e) => {
    setAccount(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };
  console.log(userName, account, password);
  const handleRegister = (e) => {
    e.preventDefault();
    AuthService.register(userName, account, password)
      .then(() => {
        window.alert("註冊成功。您現在將被導向到登入頁面");
        navigate("/login");
      })
      .catch((e) => {
        console.log(e.response.data);
      });
  };
  return (
    <form>
      <label htmlFor="username">使用者名稱:</label>
      <input onChange={handleUserName} type="String" name="username" required />
      <label htmlFor="account">帳號:</label>
      <input onChange={handleAccount} type="String" name="account" required />
      <label htmlFor="password">密碼:</label>
      <input
        onChange={handlePassword}
        type="String"
        name="password"
        required="true"
      />
      <button onClick={handleRegister}>註冊</button>
    </form>
  );
};

export default Register;
