import React, { useState } from "react";
import AuthService from "../services/auth-service";
import { useNavigate } from "react-router-dom";
const EditPassword = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const userId = currentUser.user._id;
  const handleFormSubmit = (event) => {
    event.preventDefault();

    if (newPassword !== confirmPassword) {
      setErrorMessage("新密碼和確認密碼不一致");
      return;
    }

    const updatedItem = {
      currentPassword,
      newPassword,
    };

    AuthService.editPassword(userId, updatedItem)
      .then((response) => {
        logOut();
        setSuccessMessage("密碼已成功更新");
        setErrorMessage(null);
        navigate("/loginPage");
      })
      .catch((error) => {
        setErrorMessage("密碼更新失敗");
        setSuccessMessage(null);
      });
  };

  const logOut = () => {
    AuthService.logout(); // 清空local storage
    window.alert("密碼已更新，請重新登入！");
    setCurrentUser(null);
  };

  return (
    <div>
      <form className="editPassWord">
        <label>目前密碼:</label>
        <input
          type="password"
          value={currentPassword}
          placeholder="舊密碼"
          onChange={(e) => setCurrentPassword(e.target.value)}
        />
        <label>新密碼:</label>
        <input
          type="password"
          value={newPassword}
          placeholder="新密碼(至少6個字元)"
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <label>確認新密碼:</label>
        <input
          type="password"
          value={confirmPassword}
          placeholder="確認新密碼"
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        {errorMessage && <div className="error-message">{errorMessage}</div>}
        {successMessage && (
          <div className="success-message">{successMessage}</div>
        )}
        <button onClick={handleFormSubmit}>更新</button>
      </form>
    </div>
  );
};

export default EditPassword;
