import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ProductService from "../services/product-service";

const Category = () => {
  const navigate = useNavigate();
  const { category } = useParams();
  let [productData, setProductData] = useState(null);
  console.log(productData);
  useEffect(() => {
    ProductService.getCategory(category)
      .then((data) => {
        setProductData(data.data.foundProduct);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const handleProductClick = (productId) => {
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    navigate(`/products/${productId}`);
  };
  useEffect(() => {
    if (productData) {
      const soldOutElements = document.querySelectorAll(".productImg");
      soldOutElements.forEach((product) => {
        if (product.parentElement.children[2].innerText === "0") {
          console.log(product.children);
          product.children[1].style.display = "block";
          product.classList.add("sold-out");
        }
      });
    }
  }, [productData]);
  return (
    <div>
      <div className="container">
        <div className=" g-1 row row-cols-xl-4  row-cols-lg-4 row-cols-md-3">
          {Array.isArray(productData) &&
            productData.map((product) => {
              return (
                <div
                  className="col"
                  key={product._id}
                  onClick={() => handleProductClick(product._id)}
                >
                  <div className="productImg">
                    <img
                      className="img-soldout"
                      src={product.imageUrl[0]}
                      alt="圖片"
                      style={{ width: "200px", height: "auto" }}
                    />
                    <div className="soldout-item-content">
                      <div>售完</div>
                    </div>
                  </div>
                  <div>{product.name}</div>
                  <div>{product.quantity}</div>
                  <div>{product.price}</div>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
};

export default Category;
