import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ProductService from "../services/product-service";
const PostProduct = () => {
  const navigate = useNavigate();
  let [name, setname] = useState("");
  let [quantity, setQuantity] = useState("");
  let [price, setPrice] = useState("");
  let [images, setImages] = useState("");
  const [selectedImages, setSelectedImages] = useState([]);
  let [message, setMessage] = useState("");
  console.log(selectedImages);
  const handleName = (e) => {
    setname(e.target.value);
  };
  const handleQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const handlePrice = (e) => {
    setPrice(e.target.value);
  };
  //   const handleImages = (e) => {
  //     const files = Array.from(e.target.files);
  //     const filename = files.map((file) => file.name);
  //     setSelectedImages(files);
  //     setImages(filename);

  //   };
  const handleImages = (e) => {
    const files = Array.from(e.target.files);
    const filenames = files.map((file) => file.name);
    setSelectedImages(files);
    setImages(filenames);
  };
  const handleImageUpload = () => {
    const formData = new FormData();
    selectedImages.forEach((image, index) => {
      formData.append(`image${index + 1}`, image);
    });
    console.log("formData", formData);

    return ProductService.upLoadImages(formData)
      .then((response) => {
        console.log(response.data.message);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handlePostProduct = (e) => {
    e.preventDefault();
    handleImageUpload()
      .then(() => {
        ProductService.post(name, quantity, price, images)
          .then(() => {
            window.alert("新增商品成功，回到個人頁面");
            navigate("/profile");
          })
          .catch((e) => {
            setMessage(e.response.data);
          });
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      {message && <div className="alert-error">{message}</div>}
      <form>
        <label htmlFor="name">商品名稱:</label>
        <input onChange={handleName} type="String" name="name" required />
        <label htmlFor="quantity">數量:</label>
        <input
          onChange={handleQuantity}
          type="number"
          name="quantity"
          required
        />
        <label htmlFor="price">價錢:</label>
        <input onChange={handlePrice} type="number" name="price" required />
        <label htmlFor="price">Images:</label>
        <input
          multiple
          onChange={handleImages}
          type="file"
          name="images"
          required
        />
        <button onClick={handlePostProduct}>新增商品</button>
      </form>
    </div>
  );
};

export default PostProduct;
