import React, { useState } from "react";

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const handleSwitchForm = () => {
    setIsLogin(!isLogin);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 根據 isLogin 狀態執行登入或註冊的相應邏輯
    if (isLogin) {
      // 登入邏輯
    } else {
      // 註冊邏輯
    }
  };

  return (
    <div>
      <button onClick={handleSwitchForm}>登入</button>
      <button onClick={handleSwitchForm}>註冊</button>

      <form onSubmit={handleSubmit}>
        {isLogin ? (
          // 登入表單內容
          <div>
            <label htmlFor="account">帳號:</label>
            <input type="String" name="account" />
            <label htmlFor="password">密碼:</label>
            <input type="String" name="password" />
          </div>
        ) : (
          // 註冊表單內容
          <div>
            <label htmlFor="username">使用者名稱:</label>
            <input
              //   onChange={handleUserName}
              type="String"
              name="username"
              required
            />
            <label htmlFor="account">帳號:</label>
            <input
              //   onChange={handleAccount}
              type="String"
              name="account"
              required
            />
            <label htmlFor="password">密碼:</label>
            <input
              //   onChange={handlePassword}
              type="String"
              name="password"
              required
            />
          </div>
        )}
        <button type="submit">{isLogin ? "登入" : "註冊"}</button>
      </form>
      <p></p>
    </div>
  );
};

export default LoginPage;
