import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import Homepage from "./components/homepage-component";
import Register from "./components/register";
import Login from "./components/login";
import EditProductPage from "./components/editProductPage-component";
import "./styles/style.css";
import AuthService from "./services/auth-service";
import PostProduct from "./components/postProduct-component";
import ProductSelf from "./components/productSelf-component";
import ProductDetail from "./components/productDetail-component";
import LoginPage from "./components/loginpage-components";
import EditProduct from "./components/editProduct-component";
import Cart from "./components/cart.component";
import ProfilePage from "./components/profilePage-component";
import EditPassword from "./components/editPassword-component";
function App() {
  let [currentUser, setCurrentUser] = useState(AuthService.getCurrentUser());
  // console.log(currentUser);
  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <Layout currentUser={currentUser} setCurrentUser={setCurrentUser} />
          }
        >
          <Route index element={<Homepage />}></Route>
          <Route
            path="/editProductPage"
            element={
              <EditProductPage
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          ></Route>
          <Route path="register" element={<Register />}></Route>
          <Route
            path="login"
            element={
              <Login
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          ></Route>
          <Route path="post" element={<PostProduct />}></Route>
          <Route
            path="profilePage"
            element={
              <ProfilePage
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          ></Route>
          <Route path="product/:_id" element={<ProductSelf />}></Route>
          <Route path="product/edit/:_id" element={<EditProduct />}></Route>
          <Route path="products/:_id" element={<ProductDetail />}></Route>
          <Route path="cart" element={<Cart />}></Route>
          <Route path="editpassword" element={<EditPassword />}></Route>
          <Route
            path="loginpage"
            element={
              <LoginPage
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          ></Route>
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
