import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";

const Profile = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const [imageUrls, setImageUrls] = useState([]);
  let [productData, setProductData] = useState(null);
  console.log(productData);
  useEffect(() => {
    const fetchImageUrls = async () => {
      try {
        const response = await axios.get("/api/images"); // 发起GET请求以获取图像数据
        const data = response.data;
        const urls = data.map((imageName) => `/images/${imageName}`); // 根据服务器存储路径构建图像URL
        setImageUrls(urls);
      } catch (error) {
        console.log(error);
      }
    };

    fetchImageUrls();
  }, []);

  useEffect(() => {
    ProductService.get()
      .then((data) => {
        if (Array.isArray(data.data)) {
          setProductData(data.data);
        } else {
          console.log("Invalid data format");
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const handleDelete = () => {
    AuthService.deleteUser(currentUser.user._id)
      .then((response) => {
        alert(response.data.message);
        setCurrentUser(null);
        navigate("/");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      {!currentUser && <div>請先登入</div>}
      {currentUser && (
        <div>
          <div>{currentUser.user.username} </div>
          {productData && <p>刊登商品 </p>}
          {!productData && <p>無刊登商品 </p>}
          {productData &&
            productData.map((product) => {
              return (
                <div
                  key={product._id}
                  onClick={() => handleProductClick(product._id)}
                >
                  {/* <img src={product.images[0]} alt="product" /> */}
                  {imageUrls.map((imageUrl, index) => (
                    <img key={index} src={imageUrl} alt={`Image ${index}`} />
                  ))}
                  <div>{product.name}</div>
                  <div>{product.quantity}</div>
                  <div>{product.price}</div>
                </div>
              );
            })}
        </div>
      )}
      <button onClick={handleDelete}>刪除帳號</button>
    </div>
  );
};

export default Profile;
