import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faCartShopping,
  faMagnifyingGlass,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const Nav = ({ currentUser, setCurrentUser, searchData, setSearchData }) => {
  const navigate = useNavigate();
  let [cartData, setCartData] = useState(null);
  let [cartQuantity, setCartQuantity] = useState(0);
  let [cartItem, setCartItem] = useState(null);

  console.log(cartItem);
  const [showInput, setShowInput] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [keyword, setKeyword] = useState("");

  useEffect(() => {
    getCartItems();
  }, []);
  const getCartItems = () => {
    CartService.getCart()
      .then((data) => {
        setCartItem(data.data.items);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleLinkHover = () => {
    setShowInput(true);
  };

  const handleLinkLeave = () => {
    setShowInput(false);
  };

  const handleInputChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      setSearchData(keyword);
      setKeyword("");
      navigate("/search");
    }
  };
  const inputStyles = {
    display: "inline-block",
    zIndex: "100",
    position: "absolute",
    border: "none",
    borderBottom: "0.75px solid black",
    width: showInput ? "8rem" : "0", // 根據 showInput 狀態設置寬度
    transition: "width 0.5s ease", // 指定寬度屬性的過渡效果
    fontSize: "1.25rem",
  };
  useEffect(() => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        let quantity = cartData.length;
        setCartQuantity(quantity);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  const handleDelete = (productId) => {
    return () => {
      CartService.productDelete(productId)
        .then(() => {
          alert("商品已刪除");
          getCartItems();
        })
        .catch((e) => {
          console.log(e);
        });
    };
  };
  return (
    <div
      style={{
        borderBottom: "0.5px solid black",
        paddingBottom: "1.5rem",
      }}
    >
      {showCart && (
        <div className="showCart-Container">
          <div className="cartList">
            <p>Shopping Cart</p>
            <div className="cartItem">
              {cartItem &&
                cartItem.map((item) => {
                  return (
                    <div className="item" key={item._id}>
                      <img src={item.product.imageUrl[0]} alt="productImg" />
                      <div className="itemName">{item.product.name}</div>
                      <div className="itemQuantity">{item.quantity}x</div>
                      <div className="itemPrice">NT${item.product.price}</div>
                      <button onClick={handleDelete(item.product._id)}>
                        <FontAwesomeIcon
                          icon={faTrash}
                          style={{ color: "#a8a7a7" }}
                        />
                      </button>
                    </div>
                  );
                })}
              <butto onClick={navigate("/cart")}>checkOut</butto>
            </div>
          </div>
          <div
            onClick={() => {
              setShowCart(false);
            }}
            className="shadow"
          ></div>
        </div>
      )}
      <nav>
        <ul className="nav-left">
          <li className="leftitem">
            <span>$ TWD</span>
          </li>
          <li className="leftitem">
            <span>繁體中文</span>
          </li>
        </ul>

        <Link to={"/"} className="link-style nav-start">
          <img
            src="/images/goopiLogo.webp"
            alt=""
            style={{
              width: "200px",
              height: "140px",
              margin: "0rem",
              padding: "0rem",
            }}
          />
        </Link>

        <ul className="nav-right">
          <li className="rightitem">
            <FontAwesomeIcon
              onMouseEnter={handleLinkHover}
              className="link-style nav-end"
              icon={faMagnifyingGlass}
              style={{ color: "#000000" }}
            />

            {showInput && (
              <input
                type="text"
                value={keyword}
                placeholder="找商品"
                onChange={handleInputChange}
                onMouseLeave={handleLinkLeave}
                onKeyDown={handleInputKeyDown}
                style={{
                  display: "inline-block",
                  zIndex: "100",
                  position: "absolute",
                  border: "none",
                  borderBottom: "0.75px solid black",
                  width: "8rem",
                  transition: "all 0.5s ease",
                  fontSize: "1.25rem",
                }}
              />
            )}
          </li>
          {currentUser ? (
            <li className="rightitem">
              <Link to={"/profilePage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />{" "}
              </Link>
            </li>
          ) : (
            <li className="rightitem">
              <Link to={"/loginPage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />{" "}
              </Link>
            </li>
          )}

          <li
            onClick={() => {
              setShowCart(true);
            }}
            className="rightitem"
          >
            <FontAwesomeIcon
              icon={faCartShopping}
              style={{ color: "#000000" }}
            />
            {/* <Link to={"/cart"} className="link-style nav-end">
              <FontAwesomeIcon
                icon={faCartShopping}
                style={{ color: "#000000" }}
              />
            </Link> */}

            {cartQuantity > 0 && (
              <span className="cart-item-count">{cartQuantity}</span>
            )}
          </li>
        </ul>
      </nav>
      <div className="nav-bar">
        <ul>
          <li
            onClick={() => {
              navigate("/products/categories/new-arrivals");
            }}
            className="bar"
          >
            NEW ARRIVALS
          </li>
          <li
            onClick={() => {
              navigate("/products/categories/category");
            }}
            className="drop-down-menu"
          >
            CATEGORY <span className="caret"></span>
            <ul className="contents">
              <li
                onClick={(e) => {
                  e.stopPropagation();
                  navigate("/products/categories/TOP");
                }}
              >
                {" "}
                TOP
              </li>
              <li
                onClick={(e) => {
                  e.stopPropagation();
                  navigate("/products/categories/BOTTOM");
                }}
              >
                BOTTOM
              </li>
              <li
                onClick={(e) => {
                  e.stopPropagation();
                  navigate("/products/categories/ACCESSORIES");
                }}
              >
                ACCESSORIES
              </li>
              <li
                onClick={(e) => {
                  e.stopPropagation();
                  navigate("/products/categories/HEADWEAR");
                }}
              >
                HEADWEAR
              </li>
            </ul>
          </li>
          <li
            className="drop-down-menu"
            onClick={() => {
              navigate("/products/categories/brands");
            }}
          >
            BRANDS<span className="caret"></span>
