import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ProductService from "../services/product-service";
const PostProduct = () => {
  const navigate = useNavigate();
  let [name, setname] = useState("");
  let [quantity, setQuantity] = useState("");
  let [price, setPrice] = useState("");
  const [selectedImage, setSelectedImage] = useState(null);

  let [message, setMessage] = useState("");
  console.log(images);
  const handleName = (e) => {
    setname(e.target.value);
  };
  const handleQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const handlePrice = (e) => {
    setPrice(e.target.value);
  };
  //   const handleImages = (e) => {
  //     const files = Array.from(e.target.files);
  //     const filename = files.map((file) => file.name);
  //     setSelectedImages(files);
  //     setImages(filename);

  //   };
  const handleImageChange = (e) => {
    setSelectedImage(e.target.files[0]);
  };

  const handlePostProduct = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", name);
    formData.append("quantity", quantity);
    formData.append("price", price);
    images.forEach((image) => {
      formData.append("images", image);
      console.log(formData);
    });
    ProductService.post(formData)
      .then(() => {
        window.alert("新增商品成功，回到個人頁面");
        navigate("/profile");
      })
      .catch((e) => {
        setMessage(e.response.data);
      });
  };

  return (
    <div>
      {message && <div className="alert-error">{message}</div>}
      <form>
        <label htmlFor="name">商品名稱:</label>
        <input onChange={handleName} type="String" name="name" required />
        <label htmlFor="quantity">數量:</label>
        <input
          onChange={handleQuantity}
          type="number"
          name="quantity"
          required
        />
        <label htmlFor="price">價錢:</label>
        <input onChange={handlePrice} type="number" name="price" required />
        <label htmlFor="price">Images:</label>
        <input
          multiple
          onChange={handleImageChange}
          type="file"
          name="images"
          required
        />
        <button onClick={handlePostProduct}>新增商品</button>
      </form>
    </div>
  );
};

export default PostProduct;
