import React, { useEffect, useState } from "react";
import AuthService from "../services/auth-service";
const ProfilePage = ({ currentUser, setCurrentUser }) => {
  const _id = currentUser.user._id;
  let [userData, setUserData] = useState("");
  let a = userData[0].account;

  let [account, setAccount] = useState(a);
  console.log(userData[0].account);
  const handelAccount = (e) => {
    setAccount(e.target.value);
  };
  useEffect(() => {
    AuthService.getUser(_id)
      .then((data) => {
        setUserData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  console.log(userData);
  return (
    <div>
      {userData && (
        <div>
          <div>{userData[0].username}</div>
          <p>編輯會員資料</p>
          <input type="string" value={account} onChange={handelAccount} />
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
