import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useParams, useNavigate } from "react-router-dom";

const ProductSelf = () => {
  const navigate = useNavigate();
  let [productData, setProductData] = useState(null);
  console.log(productData);
  const { _id } = useParams();

  useEffect(() => {
    ProductService.getById(_id)
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, [_id]);

  const handleDelete = () => {
    ProductService.delete(_id)
      .then(() => {
        window.alert("商品已刪除");
        navigate("/editproductpage");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleEdit = () => {
    navigate(`/product/edit/${_id}`);
  };

  return (
    <div>
      {productData && productData.foundProduct && (
        <div className="productSelf">
          <div className="ImgAll">
            {productData.foundProduct.imageUrl.map((url, index) => {
              return (
                <div key={index}>
                  <img
                    src={url}
                    alt="圖片"
                    style={{
                      width: "200px",
                      height: "auto",
                    }}
                    onError={(e) => console.log("图片加载失败", e)}
                  />
                </div>
              );
            })}
          </div>

          <div>{productData.foundProduct.name}</div>
          <div>{productData.foundProduct.quantity}</div>
          <div>{productData.foundProduct.price}</div>
          <button onClick={handleEdit}>Edit</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      )}
    </div>
  );
};

export default ProductSelf;
