import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import AuthService from "../services/auth-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faCartShopping } from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const Nav = ({ currentUser, setCurrentUser }) => {
  console.log(currentUser);
  let [cartData, setCartData] = useState(null);
  let [cartQuantity, setCartQuantity] = useState(0);
  const handleLogout = () => {
    AuthService.logout(); // 清空local storage
    window.alert("登出成功!現在您會被導向到首頁。");
    setCurrentUser(null);
    console.log(currentUser);
  };
  useEffect(() => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        let quantity = cartData.length;
        setCartQuantity(quantity);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  useEffect(() => {
    setCartQuantity(cartData.length);
  }, [cartData]);
  console.log(cartData.length);
  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to={"/"} className="link-style nav-start">
              主頁
            </Link>
          </li>
          {currentUser && (
            <li>
              <Link to={"/editProductPage"} className="link-style nav-start">
                編輯商品
              </Link>
            </li>
          )}

          {!currentUser && (
            <li>
              <Link to={"/loginPage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}

          {currentUser && (
            <li>
              <Link to={"/profilePage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}

          {currentUser && (
            <li>
              <Link to={"/post"} className="link-style nav-end">
                賣東西
              </Link>
            </li>
          )}
          {currentUser && (
            <li>
              <Link to={"/cart"} className="link-style nav-end">
                <FontAwesomeIcon
                  icon={faCartShopping}
                  style={{ color: "#000000" }}
                />
              </Link>
              {cartQuantity > 0 && (
                <span className="cart-item-count">{cartQuantity}</span>
              )}
            </li>
          )}
          {currentUser && (
            <li>
              <Link
                onClick={handleLogout}
                className="link-style nav-start"
                to="/"
              >
                登出
              </Link>
            </li>
          )}
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
