import React, { useState } from "react";
import { Link } from "react-router-dom";
import AuthService from "../services/auth-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faCartShopping } from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const Nav = ({ currentUser, setCurrentUser }) => {
  console.log(currentUser);
  const [cartItemCount, setCartItemCount] = useState(0);
  let [cartData, setCartData] = useState(null);
  let [quantityData, setQuantityData] = useState([]);

  const handleLogout = () => {
    AuthService.logout(); // 清空local storage
    window.alert("登出成功!現在您會被導向到首頁。");
    setCurrentUser(null);
    console.log(currentUser);
  };

  const getCartItems = () => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        const initialQuantityData = data.data.items.map(
          (item) => item.quantity
        );
        setQuantityData(initialQuantityData);

        // 计算购物车中商品的总数量
        const itemCount = initialQuantityData.reduce(
          (acc, curr) => acc + curr,
          0
        );
        setCartItemCount(itemCount);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to={"/"} className="link-style nav-start">
              主頁
            </Link>
          </li>
          {currentUser && (
            <li>
              <Link to={"/editProductPage"} className="link-style nav-start">
                編輯商品
              </Link>
            </li>
          )}

          {!currentUser && (
            <li>
              <Link to={"/loginPage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}

          {currentUser && (
            <li>
              <Link to={"/profilePage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}

          {currentUser && (
            <li>
              <Link to={"/post"} className="link-style nav-end">
                賣東西
              </Link>
            </li>
          )}
          {currentUser && (
            <li>
              <Link to={"/cart"} className="link-style nav-end">
                <FontAwesomeIcon
                  icon={faCartShopping}
                  style={{ color: "#000000" }}
                />
                {cartItemCount > 0 && (
                  <span className="cart-item-count">{cartItemCount}</span>
                )}
              </Link>
            </li>
          )}
          {currentUser && (
            <li>
              <Link
                onClick={handleLogout}
                className="link-style nav-start"
                to="/"
              >
                登出
              </Link>
            </li>
          )}
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
