import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBagShopping,
  faPlus,
  faMinus,
} from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const ProductALL = ({ productData, setShowCart }) => {
  const navigate = useNavigate();
  console.log(productData);
  const [reversedProductData, setReversedProductData] = useState([]);
  const [showCartItem, setShowCartItem] = useState(false);
  const [hoveredProductId, setHoveredProductId] = useState(null);
  const [showQuickShop, setShowQuickShop] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedProductId, setSelectedProductId] = useState(null);
  let [quantity, setQuantity] = useState(1);
  let [mainImg, setMainImg] = useState("");
  console.log(selectedProduct);

  const hadelQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const handleIncrement = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };
  const handelCartItemEnter = (productId) => {
    setShowCartItem(!showCartItem);
    setHoveredProductId(productId);
  };
  const handelCartItemLeave = (productId) => {
    setShowCartItem(!showCartItem);
    setHoveredProductId(null);
  };
  useEffect(() => {
    if (productData) {
      setReversedProductData([...productData].reverse());
    }
  }, [productData]);
  const handleProductClick = (productId) => {
    const url = `/products/${productId}`;
    window.open(url, "_blank");
  };
  const handleQuickShop = (productId, e) => {
    e.stopPropagation();
    const selected = productData.find((product) => product._id === productId);
    setSelectedProduct(selected);
    if (selected) {
      setMainImg(selected.imageUrl[0]);
      setSelectedProductId(selected._id);
    }
    setShowQuickShop(true);
  };
  const hadelCartAdd = (e) => {
    e.preventDefault();
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    if (productData && productData.foundProduct) {
      CartService.CartAdd(setHoveredProductId, quantity)
        .then(() => {
          window.alert("商品已新增至購物車");
          setQuantity(1);
          setShowCart(true);
        })
        .catch((e) => {
          alert(e.response.data);
        });
    }
  };
  const hadelCheckOut = (e) => {
    console.log(1);
    e.preventDefault();
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    if (productData && productData.foundProduct) {
      CartService.CartAdd(setHoveredProductId, quantity)
        .then(() => {
          window.alert("前往結帳畫面");
          navigate("/cart");
        })
        .catch((e) => {
          alert(e.response.data);
        });
    }
  };
  return (
    <div>
      <div className="container">
        {showQuickShop && (
          <div className="quickShop-container">
            <div className="quickShop">
              <div className="imgShow">
                <div className="mainImg">
                  <img src={mainImg} alt="" />
                </div>
                <div className="allImg">
                  {selectedProduct.imageUrl.map((url, index) => {
                    return (
                      <div
                        onClick={() => {
                          setMainImg(url);
                        }}
                        className="imgItem"
                        key={index}
                      >
                        <img src={url} alt="圖片" />
                      </div>
                    );
                  })}
                </div>
              </div>
              <div className="rightItem">
                <h3>{selectedProduct.name}</h3>
                <p>Price: NT${selectedProduct.price}</p>
                <div className="quantitySet">
                  {" "}
                  <label htmlFor="quantitu">Quantity </label>
                  <input
                    type="number"
                    min={1}
                    onChange={hadelQuantity}
                    value={quantity}
                    readOnly
                  />
                  <div className="control">
                    <div onClick={handleIncrement}>
                      <FontAwesomeIcon
                        icon={faPlus}
                        style={{ color: "#000000" }}
                      />
                    </div>
                    <div onClick={handleDecrement}>
                      <FontAwesomeIcon
                        icon={faMinus}
                        style={{ color: "#000000" }}
                      />
                    </div>
                  </div>
                </div>

                <div className="detailSubmit">
                  <button
                    onClick={() => {
                      hadelCartAdd();
                    }}
                  >
                    加入購物車
                  </button>
                  <button
                    onClick={() => {
                      hadelCheckOut();
                    }}
                  >
                    直接購買
                  </button>
                </div>
              </div>
            </div>
            <div className="shadow"></div>
          </div>
        )}
        <div className=" g-3 row row-cols-xl-4    row-cols-lg-3 row-cols-md-2 row-cols-sm-2  row-cols-1">
          {reversedProductData &&
            reversedProductData.reverse().map((product) => {
              return (
                <div
                  className="col text-center"
                  key={product._id}
                  onClick={() => handleProductClick(product._id)}
                  style={{ margin: "1.5rem 0rem" }}
                >
                  <div
                    className="productImg"
                    onMouseEnter={() => {
                      handelCartItemEnter(product._id);
                    }}
                    onMouseLeave={() => {
                      handelCartItemLeave(product._id);
                    }}
                  >
                    {product.quantity == 0 ? (
                      <div className="sold-out">
                        <img
                          src={product.imageUrl[0]}
                          alt="圖片"
                          className="sold-out-img"
                        />
                      </div>
                    ) : (
                      <img
                        src={product.imageUrl[0]}
                        alt="圖片"
                        className="normal-img"
                      />
                    )}
                    {showCartItem && hoveredProductId == product._id && (
                      <div
                        className="cartAddItem"
                        onClick={(e) => {
                          handleQuickShop(product._id, e);
                        }}
                      >
                        <FontAwesomeIcon
                          icon={faBagShopping}
                          style={{ color: "#ff7b1a" }}
                        />
                      </div>
                    )}
                    {product.quantity == 0 && (
                      <div className="soldout-item-content">
                        <div>SOLD</div>
                      </div>
                    )}
                  </div>

                  <div>{product.name}</div>
                  {/* <div>{product.quantity}</div> */}
                  <div>NT${product.price}</div>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
};

export default ProductALL;
