import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useParams } from "react-router-dom";
import CartService from "../services/cart-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";
const ProductDetail = () => {
  const navigate = useNavigate();
  let [productData, setProductData] = useState(null);
  let [quantity, setQuantity] = useState(1);
  const { _id } = useParams();
  useEffect(() => {
    ProductService.getById(_id)
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const hadelQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const handleIncrement = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };
  const hadelCartAdd = (e) => {
    e.preventDefault();
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    if (productData && productData.foundProduct) {
      CartService.CartAdd(_id, quantity)
        .then(() => {
          window.alert("商品已新增至購物車");
          setQuantity(1);
        })
        .catch((e) => {
          alert(e.response.data);
        });
    }
  };
  const hadelCheckOut = (e) => {
    e.preventDefault();
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    if (productData && productData.foundProduct) {
      CartService.CartAdd(_id, quantity)
        .then(() => {
          window.alert("前往結帳畫面");
          navigate("/cart");
        })
        .catch((e) => {
          alert(e.response.data);
        });
    }
  };
  return (
    <div>
      {productData && productData.foundProduct && (
        <div>
          <div className="imgShow">
            {productData.foundProduct.imageUrl.map((url, index) => {
              return (
                <div className="imgItem" key={index}>
                  <img
                    src={url}
                    alt="圖片"
                    style={{
                      width: "200px",
                      height: "auto",
                    }}
                  />
                </div>
              );
            })}
          </div>

          <div>{productData.foundProduct.name}</div>
          <div>{productData.foundProduct.price}</div>

          <label htmlFor="quantitu">數量 </label>

          <input
            type="number"
            min={1}
            onChange={hadelQuantity}
            value={quantity}
          />
          <span onClick={handleIncrement}>
            <FontAwesomeIcon icon={faPlus} style={{ color: "#000000" }} />
          </span>
          <span onClick={handleDecrement}>
            <FontAwesomeIcon icon={faMinus} style={{ color: "#000000" }} />
          </span>
          <button onClick={hadelCartAdd}>加入購物車</button>
          <button onClick={hadelCheckOut}>直接購買</button>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
