import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useParams, useNavigate } from "react-router-dom";

const EditProduct = () => {
  const navigate = useNavigate();
  const { _id } = useParams();
  const [productData, setProductData] = useState(null);
  const [name, setName] = useState();
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const [iamges, setImages] = useState(null);
  let [selectedImages, setSelectedImages] = useState(null);
  useEffect(() => {
    ProductService.getById(_id)
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, [_id]);
  useEffect(() => {
    if (productData) {
      setName(productData.foundProduct.name);
      setQuantity(productData.foundProduct.quantity);
      setPrice(productData.foundProduct.price);
    }
  }, [productData]);

  const handleEdit = () => {
    let urls = selectedImages.map((name) => `/images/${name}`);

    const updateItem = {
      name,
      quantity,
      price,
      imageUrl: urls,
    };

    ProductService.editProduct(_id, updateItem)
      .then(() => {
        window.alert("商品已編輯成功");
        navigate(`/editProductPage`);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const fileNames = files.map((file) => file.name);
    setSelectedImages(fileNames);
  };
  return (
    <div>
      {productData && (
        <div>
          <div>
            <label>商品名稱:</label>
            {productData && (
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            )}
          </div>
          <div>
            <label>數量:</label>
            {productData && (
              <input
                type="number"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
              />
            )}
          </div>
          <div>
            <label>價格:</label>
            {productData && (
              <input
                type="number"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            )}
          </div>
          <label>圖片</label>
          <input
            multiple
            onChange={handleImageChange}
            type="file"
            name="images"
          />
          <button onClick={handleEdit}>儲存</button>
        </div>
      )}
    </div>
  );
};

export default EditProduct;
