import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ProductService from "../services/product-service";
const PostProduct = () => {
  const navigate = useNavigate();
  let [name, setname] = useState("");
  let [quantity, setQuantity] = useState("");
  let [price, setPrice] = useState("");
  let [images, setImages] = useState("");
  let [message, setMessage] = useState("");

  const handleName = (e) => {
    setname(e.target.value);
  };
  const handleQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const handlePrice = (e) => {
    setPrice(e.target.value);
  };
  const handleImages = (e) => {
    const files = Array.from(e.target.files);
    const filename = files.map((name) => filename);
    setImages(filename);
  };
  const handlePostProduct = (e) => {
    e.preventDefault();
    ProductService.post(name, quantity, price)
      .then(() => {
        window.alert("新增商品成功，回到個人頁面");
        navigate("/profile");
      })
      .catch((e) => {
        setMessage(e.response.data);
      });
  };
  return (
    <div>
      {message && <div className="alert-error">{message}</div>}
      <form>
        <label htmlFor="name">商品名稱:</label>
        <input onChange={handleName} type="String" name="name" required />
        <label htmlFor="quantity">數量:</label>
        <input
          onChange={handleQuantity}
          type="number"
          name="quantity"
          required
        />
        <label htmlFor="price">價錢:</label>
        <input onChange={handlePrice} type="number" name="price" required />
        <label htmlFor="price">Images:</label>
        <input onChange={handleImages} type="file" name="images" required />
        <button onClick={handlePostProduct}>新增商品</button>
      </form>
    </div>
  );
};

export default PostProduct;
