import React from "react";
import { Link } from "react-router-dom";
import AuthService from "../services/auth-service";
const nav = ({ currentUser, setCurrentUser }) => {
  console.log(currentUser);
  const handleLogout = () => {
    AuthService.logout(); // 清空local storage
    window.alert("登出成功!現在您會被導向到首頁。");
    setCurrentUser(null);
  };
  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to={"/"} className="link-style nav-start">
              主頁
            </Link>
          </li>
          {currentUser && (
            <li>
              <Link to={"/profile"} className="link-style nav-start">
                個人頁面
              </Link>
            </li>
          )}

          {!currentUser && (
            <li>
              <Link to={"/register"} className="link-style nav-end">
                註冊
              </Link>
            </li>
          )}
          {!currentUser && (
            <li>
              <Link to={"/login"} className="link-style nav-end">
                登入
              </Link>
            </li>
          )}
          {currentUser && (
            <li>
              <Link to={"/post"} className="link-style nav-end">
                賣東西
              </Link>
            </li>
          )}

          <li>
            <Link onClick={handleLogout} to="/">
              登出
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default nav;
