import React, { useEffect, useState } from "react";
import AuthService from "../services/auth-service";
const ProfilePage = ({ currentUser, setCurrentUser }) => {
  const _id = currentUser.user._id;
  let [userData, setUserData] = useState("");
  let [account, setAccount] = useState("");
  let [userName, setUserName] = useState("");
  useEffect(() => {
    if (userData) {
      setAccount(userData[0].account);
      setUserName(userData[0].userName);
    }
  }, [userData]);

  const handelAccount = (e) => {
    setAccount(e.target.value);
  };

  useEffect(() => {
    AuthService.getUser(_id)
      .then((data) => {
        setUserData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  const updatedItem = { userName, account };
  const handleSubmit = () => {
    AuthService.editUser(_id, updatedItem)
      .then(() => {
        alert("更改成功");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      {userData && (
        <div>
          <div>{userData[0].username}</div>
          <p>編輯會員資料</p>
          <label>姓名</label>
          <input
            type="string"
            value={userName}
            onChange={(e) => {
              setUserName(e.target.value);
            }}
          />
          <label>帳號</label>
          <input type="string" value={account} onChange={handelAccount} />
          <button onClick={handleSubmit}>儲存變更</button>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
