import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
const LoginPage = ({ currentUser, setCurrentUser }) => {
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();
  let [userName, setUserName] = useState("");
  let [account, setAccount] = useState("");
  let [password, setPassword] = useState("");
  let [message, setMessage] = useState("");
  const handleUserName = (e) => {
    setUserName(e.target.value);
  };
  const handleAccount = (e) => {
    setAccount(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };
  const handleSwitchForm = () => {
    setIsLogin(!isLogin);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // 根據 isLogin 狀態執行登入或註冊的相應邏輯
    if (isLogin) {
      // 登入邏輯
      try {
        let response = await AuthService.login(account, password);
        localStorage.setItem("user", JSON.stringify(response.data));
        window.alert("登入成功。您現在將被導向到個人頁面");
        console.log(setCurrentUser);
        setCurrentUser(AuthService.getCurrentUser());
        navigate("/profile");
      } catch (e) {
        setMessage(e.response.data);
      }
    } else {
      // 註冊邏輯
      AuthService.register(userName, account, password)
        .then(() => {
          window.alert("註冊成功。您現在將被導向到登入頁面");
          setIsLogin(!isLogin);
        })
        .catch((e) => {
          setMessage(e.response.data);
        });
    }
  };

  return (
    <div>
      <button onClick={handleSwitchForm} disabled={isLogin}>
        登入
      </button>
      <button onClick={handleSwitchForm} disabled={!isLogin}>
        註冊
      </button>
      {isLogin && <p>請輸入登入資訊</p>}
      {message && <div className="alert-error">{message}</div>}
      <form onSubmit={handleSubmit}>
        {isLogin ? (
          // 登入表單內容
          <div>
            <label htmlFor="account">帳號:</label>
            <input onChange={handleAccount} type="String" name="account" />
            <label htmlFor="password">密碼:</label>
            <input onChange={handlePassword} type="String" name="password" />
          </div>
        ) : (
          // 註冊表單內容
          <div>
            <label htmlFor="username">使用者名稱:</label>
            <input
              onChange={handleUserName}
              type="String"
              name="username"
              required
            />
            <label htmlFor="account">帳號:</label>
            <input
              onChange={handleAccount}
              type="String"
              name="account"
              required
            />
            <label htmlFor="password">密碼:</label>
            <input
              onChange={handlePassword}
              type="String"
              name="password"
              required
            />
          </div>
        )}
        <button onClick={handleSubmit} type="submit">
          {isLogin ? "登入" : "註冊"}
        </button>
      </form>
      <p></p>
    </div>
  );
};

export default LoginPage;
