import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faCartShopping,
  faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const Nav = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const history = useHistory();

  let [cartData, setCartData] = useState(null);
  let [cartQuantity, setCartQuantity] = useState(0);

  const [showInput, setShowInput] = useState(false);
  const [keyword, setKeyword] = useState("");

  const handleLinkHover = () => {
    setShowInput(true);
  };

  const handleLinkLeave = () => {
    setShowInput(false);
  };

  const handleInputChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      // 傳送資料到SearchPage
      history.push(`/search?keyword=${keyword}`);
      navigate("/search");
      console.log(keyword);
    }
  };
  useEffect(() => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        let quantity = cartData.length;
        setCartQuantity(quantity);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to={"/"} className="link-style nav-start">
              主頁
            </Link>
          </li>

          {currentUser ? (
            <li>
              <Link to={"/profilePage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          ) : (
            <li>
              <Link to={"/loginPage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}
          {currentUser && (
            <li>
              <Link
                to={"/search"}
                className="link-style nav-end"
                onMouseEnter={handleLinkHover}
              >
                <FontAwesomeIcon
                  icon={faMagnifyingGlass}
                  style={{ color: "#000000" }}
                />
              </Link>
              {showInput && (
                <input
                  type="text"
                  value={keyword}
                  onChange={handleInputChange}
                  onMouseLeave={handleLinkLeave}
                  onKeyDown={handleInputKeyDown}
                />
              )}
            </li>
          )}
          {currentUser && (
            <li>
              <Link to={"/cart"} className="link-style nav-end">
                <FontAwesomeIcon
                  icon={faCartShopping}
                  style={{ color: "#000000" }}
                />
              </Link>
              {cartQuantity > 0 && (
                <span className="cart-item-count">{cartQuantity}</span>
              )}
            </li>
          )}
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
