import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useNavigate } from "react-router-dom";

const Homepage = () => {
  let [productData, setProductData] = useState(null);
  let [homeItem, setHomeItem] = useState("/images/homeItem1.webp");
  const [currentDot, setCurrentDot] = useState(0);
  console.log(productData);
  const navigate = useNavigate();
  const dots = [
    "/images/homeItem1.webp",
    "/images/homeItem2.webp",
    "/images/homeItem3.webp",
    "/images/homeItem4.webp",
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDot((prevDot) => (prevDot + 1) % dots.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    setHomeItem(dots[currentDot]);
  }, [currentDot]);

  // productData &&
  //   productData.map((product) => {
  //     if (product.quantity === 0) {
  //       soldOut.classList.add("sold-out");
  //     }
  //   });

  useEffect(() => {
    ProductService.getAll()
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/products/${productId}`);
  };

  useEffect(() => {
    if (productData) {
      const soldOutElements = document.querySelectorAll(".productImg");
      soldOutElements.forEach((product) => {
        if (product.parentElement.children[2].innerText === "0") {
          console.log(product.children);
          product.children[1].style.display = "block";
          product.classList.add("sold-out");
        }
      });
    }
  }, [productData]);
  return (
    <div>
      <div>
        <img
          src={homeItem}
          alt=""
          style={{ width: "100%", height: "auto", marginBottom: "1rem" }}
        />
        <div className="dots">
          <div className="dot-container">
            {dots.map((dot, index) => (
              <div
                onClick={() => {
                  // setHomeItem(dots[index]);
                  setCurrentDot(index % dots.length);
                }}
                className={`dot ${index === currentDot ? "active" : ""}`}
                key={index}
              ></div>
            ))}
          </div>
        </div>
      </div>

      <div className="container">
        <div className=" g-1 row row-cols-xl-4  row-cols-lg-4 row-cols-md-3 ">
          {productData &&
            productData.map((product) => {
              return (
                <div
                  className="col"
                  key={product._id}
                  onClick={() => handleProductClick(product._id)}
                >
                  <div className="productImg">
                    <img
                      className="img-soldout"
                      src={product.imageUrl[0]}
                      alt="圖片"
                      style={{ width: "200px", height: "240px" }}
                    />
                    <div className="soldout-item-content">
                      <div>售完</div>
                    </div>
                  </div>
                  <div>{product.name}</div>
                  <div>{product.quantity}</div>
                  <div>{product.price}</div>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
