import React, { useState } from "react";
import AuthService from "../services/auth-service";
const EditPassword = ({ currentUser, setCurrentUser }) => {
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const userId = currentUser.user._id;
  const handleFormSubmit = (event) => {
    event.preventDefault();

    // 检查新密码和确认密码是否匹配
    if (newPassword !== confirmPassword) {
      setErrorMessage("新密码和确认密码不匹配");
      return;
    }

    // 构建要发送的数据对象
    const updatedItem = {
      currentPassword,
      newPassword,
    };

    // 调用后端API更新密码
    AuthService.editPassword(userId, updatedItem)
      .then((response) => {
        setSuccessMessage("密碼已成功更新");
        setErrorMessage(null);
      })
      .catch((error) => {
        setErrorMessage("密碼更新失敗");
        setSuccessMessage(null);
      });
  };

  return (
    <div>
      {
        <form>
          <label>目前密碼:</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
          />
          <label>新密碼:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <label>確認新密碼:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          {errorMessage && <div className="error-message">{errorMessage}</div>}
          {successMessage && (
            <div className="success-message">{successMessage}</div>
          )}
          <button onClick={handleFormSubmit}>更新</button>
        </form>
      }
    </div>
  );
};

export default EditPassword;
