import React, { useState } from "react";
import AuthSurvice from "../services/auth-survice";
const Register = () => {
  const [userName, setUserName] = useState("");
  const [account, setAccount] = useState("");
  const [password, setPassword] = useState("");
  const handleUserName = (e) => {
    setUserName(e.target.value);
  };
  const handleAccount = (e) => {
    setAccount(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };
  const handleRegister = () => {
    AuthSurvice.register(userName, account, password)
      .then(() => {
        window.alert("註冊成功。您現在將被導向到登入頁面");
        navigate("/login");
      })
      .catch((e) => {
        setMessage(e.response.data);
      });
  };
  return (
    <form>
      <label htmlFor="username">使用者名稱:</label>
      <input onChange={handleUserName} type="text" name="username" />
      <label htmlFor="account">帳號:</label>
      <input onChange={handleAccount} type="text" name="account" />
      <label htmlFor="password">密碼:</label>
      <input onChange={handlePassword} type="password" name="password" />
      <button onClick={handleRegister}>註冊</button>
    </form>
  );
};

export default Register;
