import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth-service";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faCartShopping,
  faMagnifyingGlass,
} from "@fortawesome/free-solid-svg-icons";
import CartService from "../services/cart-service";
const Nav = ({ currentUser, setCurrentUser, searchData, setSearchData }) => {
  const navigate = useNavigate();
  let [cartData, setCartData] = useState(null);
  let [cartQuantity, setCartQuantity] = useState(0);

  const [showInput, setShowInput] = useState(false);
  const [keyword, setKeyword] = useState("");

  const handleLinkHover = () => {
    setShowInput(true);
  };

  const handleLinkLeave = () => {
    setShowInput(false);
  };

  const handleInputChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      setSearchData(keyword);
      navigate("/search");
    }
  };
  const inputStyles = {
    display: "inline-block",
    zIndex: "100",
    position: "absolute",
    border: "none",
    borderBottom: "0.75px solid black",
    opacity: showInput ? 1 : 0, // 根據 showInput 狀態設置透明度
    maxWidth: showInput ? "8rem" : "0", // 根據 showInput 狀態設置最大寬度
    transition: "opacity 0.5s ease, max-width 0.5s ease", // 指定透明度和最大寬度的過渡效果
    fontSize: "1.25rem",
  };
  useEffect(() => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        let quantity = cartData.length;
        setCartQuantity(quantity);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <div>
      <nav>
        <ul className="nav-left">
          <li className="leftitem">
            <span>$ TWD</span>
          </li>
          <li className="leftitem">
            <span>繁體中文</span>
          </li>
        </ul>

        <Link to={"/"} className="link-style nav-start">
          <img
            src="/images/goopiLogo.webp"
            alt=""
            style={{
              width: "200px",
              height: "140px",
              margin: "0rem",
              padding: "0rem",
            }}
          />
        </Link>

        <ul className="nav-right">
          <li className="rightitem">
            <FontAwesomeIcon
              onMouseEnter={handleLinkHover}
              className="link-style nav-end"
              icon={faMagnifyingGlass}
              style={{ color: "#000000" }}
            />

            {showInput && (
              <input
                type="text"
                value={keyword}
                placeholder="找商品"
                onChange={handleInputChange}
                onMouseLeave={handleLinkLeave}
                onKeyDown={handleInputKeyDown}
                style={inputStyles}
              />
            )}
          </li>
          {currentUser ? (
            <li className="rightitem">
              <Link to={"/profilePage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          ) : (
            <li className="rightitem">
              <Link to={"/loginPage"} className="link-style nav-end">
                <FontAwesomeIcon icon={faUser} style={{ color: "#000000" }} />
              </Link>
            </li>
          )}

          <li className="rightitem">
            <Link to={"/cart"} className="link-style nav-end">
              <FontAwesomeIcon
                icon={faCartShopping}
                style={{ color: "#000000" }}
              />
            </Link>
            {cartQuantity > 0 && (
              <span className="cart-item-count">{cartQuantity}</span>
            )}
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
