import React from "react";

const PostProduct = () => {
  let [userName, setUserName] = useState("");
  let [account, setAccount] = useState("");
  let [password, setPassword] = useState("");
  let [message, setMessage] = useState("");

  const handleUserName = (e) => {
    setUserName(e.target.value);
  };
  const handleAccount = (e) => {
    setAccount(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };
  const handleRegister = (e) => {
    e.preventDefault();
    AuthService.register(userName, account, password)
      .then(() => {
        window.alert("註冊成功。您現在將被導向到登入頁面");
        navigate("/login");
      })
      .catch((e) => {
        setMessage(e.response.data);
      });
  };
  return (
    <div>
      {message && <div className="alert-error">{message}</div>}
      <form>
        <label htmlFor="name">商品名稱:</label>
        <input onChange={handleName} type="String" name="name" required />
        <label htmlFor="quantity">數量:</label>
        <input
          onChange={handleAccount}
          type="number"
          name="quantity"
          required
        />
        <label htmlFor="price">價錢:</label>
        <input onChange={handlePassword} type="number" name="price" required />
        <button onClick={handleRegister}>註冊</button>
      </form>
    </div>
  );
};

export default PostProduct;
