import React, { useEffect, useState } from "react";
import CartService from "../services/cart-service";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";
import { useRef } from "react";
const Cart = () => {
  const inputRefs = useRef([]);
  const navigate = useNavigate();
  let [cartData, setCartData] = useState([]);

  let [quantityData, setQuantityData] = useState([]);
  let token;
  if (localStorage.getItem("user")) {
    token = JSON.parse(localStorage.getItem("user")).token;
  } else {
    token = "";
  }
  useEffect(() => {
    getCartItems();
  }, []);

  const getCartItems = () => {
    CartService.getCart()
      .then((data) => {
        setCartData(data.data.items);
        const initialQuantityData = data.data.items.map(
          (item) => item.quantity
        );
        setQuantityData(initialQuantityData);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handleIncreaseQuantity = (index) => {
    const updatedQuantityData = [...quantityData];
    updatedQuantityData[index] = parseInt(updatedQuantityData[index]) + 1;
    setQuantityData(updatedQuantityData);

    const inputRef = inputRefs.current[index];
    if (inputRef) {
      inputRef.value = updatedQuantityData[index];
    }

    const productID = cartData[index].product._id;
    const updateItem = { quantity: updatedQuantityData[index] };
    CartService.quantityUpdate(productID, updateItem)
      .then(() => {
        getCartItems();
        alert("已更新商品数量");
      })
      .catch((e) => {
        alert(e.response.data);
        getCartItems();
      });
  };
  const handleDecreaseQuantity = (index) => {
    const updatedQuantityData = [...quantityData];
    if (updatedQuantityData[index] > 1) {
      updatedQuantityData[index] = parseInt(updatedQuantityData[index]) - 1;
      setQuantityData(updatedQuantityData);

      const inputRef = inputRefs.current[index];
      if (inputRef) {
        inputRef.value = updatedQuantityData[index];
      }

      const productID = cartData[index].product._id;
      const updateItem = { quantity: updatedQuantityData[index] };
      CartService.quantityUpdate(productID, updateItem)
        .then(() => {
          getCartItems();
          alert("已更新商品数量");
        })
        .catch((e) => {
          alert(e.response.data);
          getCartItems();
        });
    }
  };
  const handleChangeQuantity = (productID, index) => {
    return (e) => {
      const newQuantity = e.target.value;
      console.log(newQuantity);

      const updatedQuantityData = [...quantityData]; // 创建新的数组副本
      updatedQuantityData[index] = newQuantity; // 更新数量

      setQuantityData(updatedQuantityData); // 更新quantityData数组
      updateItemPrice(index, newQuantity);
      const updateItem = { quantity: newQuantity };

      CartService.quantityUpdate(productID, updateItem)
        .then(() => {
          getCartItems();
          alert("已更新商品數量");
        })
        .catch((e) => {
          alert(e.response.data);
          getCartItems();
        });
    };
  };

  const handelCheckout = () => {
    if (token == "") {
      alert("請登入或註冊");
      navigate("/loginpage");
      return;
    }
    CartService.checkOut()
      .then((response) => {
        if (response.data) {
          window.alert(response.data + "回到首頁");
          navigate("/");
        }
      })
      .catch((e) => {
        window.alert(e.response.data);
      });
  };

  const handleDelete = (productId) => {
    return () => {
      CartService.productDelete(productId)
        .then(() => {
          alert("商品已刪除");
          getCartItems();
        })
        .catch((e) => {
          console.log(e);
        });
    };
  };

  const updateItemPrice = (index, quantity) => {
    const item = cartData[index];
    const price = item.product.price;
    const totalPrice = price * quantity;

    const updatedCartData = [...cartData];
    updatedCartData[index] = {
      ...item,
      totalPrice: totalPrice,
    };

    setCartData(updatedCartData);
  };
  return (
    <div>
      <p>購物車</p>
      {cartData &&
        cartData.map((item, index) => {
          return (
            <div key={item.product._id}>
              <img
                src={item.product.imageUrl[0]}
                alt="圖片"
                style={{ width: "200px", height: "auto" }}
              />
              <div>{item.product.name}</div>
              <span onClick={() => handleDecreaseQuantity(index)}>
                <FontAwesomeIcon icon={faMinus} style={{ color: "#000000" }} />
              </span>

              <input
                type="number"
                value={quantityData[index] || ""}
                onChange={handleChangeQuantity(item.product._id, index)}
                ref={(ref) => (inputRefs.current[index] = ref)}
              />
              <span onClick={() => handleIncreaseQuantity(index)}>
                <FontAwesomeIcon icon={faPlus} style={{ color: "#000000" }} />
              </span>
              <div>{item.totalPrice}</div>
              <button onClick={handleDelete(item.product._id)}>刪除</button>
            </div>
          );
        })}
      <button onClick={handelCheckout}>結帳</button>
    </div>
  );
};

export default Cart;
