import React, { useEffect, useState } from "react";
import AuthService from "../services/auth-service";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
const ProfilePage = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const _id = currentUser.user._id;
  console.log(currentUser.user.username);
  let [userData, setUserData] = useState("");
  let [account, setAccount] = useState("");
  let [userName, setUserName] = useState("");
  let [phoneNumber, setPhoneNumber] = useState("");
  let [lineId, setLineId] = useState("");
  console.log(phoneNumber);
  console.log(userData);
  useEffect(() => {
    if (userData) {
      setAccount(userData[0].account);
      setUserName(userData[0].username);
      setPhoneNumber(userData[0].phoneNumber);
      setLineId(userData[0].lineId);
    }
  }, [userData]);

  const handelAccount = (e) => {
    setAccount(e.target.value);
  };
  const handelPhone = (e) => {
    setPhoneNumber(e.target.value);
  };
  const handleLineId = (e) => {
    setLineId(e.target.value);
  };
  useEffect(() => {
    AuthService.getUser(_id)
      .then((data) => {
        setUserData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const handleLogout = () => {
    AuthService.logout(); // 清空local storage
    window.alert("登出成功!現在您會被導向到首頁。");
    setCurrentUser(null);
  };
  const handleSubmit = () => {
    const updatedItem = { userName, account, phoneNumber, lineId };
    AuthService.editUser(_id, updatedItem)
      .then(() => {
        alert("更改成功");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      {userData && (
        <div>
          {currentUser && (
            <Link
              onClick={handleLogout}
              className="link-style nav-start"
              to="/"
            >
              登出
            </Link>
          )}
          <ul>
            {currentUser.user.username == "admin" && (
              <li>
                <Link to={"/editProductPage"} className="link-style nav-start">
                  編輯商品
                </Link>
              </li>
            )}
          </ul>
          <div>{userData[0].username}</div>
          <p>編輯會員資料</p>
          <label>姓名</label>
          <input
            type="string"
            value={userName}
            onChange={(e) => {
              setUserName(e.target.value);
            }}
          />
          <label>帳號</label>
          <input type="string" value={account} onChange={handelAccount} />
          <label>密碼</label>
          <Link to="/editpassword">設定新的密碼</Link>
          <label>手機號碼</label>
          <input type="string" value={phoneNumber} onChange={handelPhone} />
          <label>LineId</label>
          <input type="string" value={lineId} onChange={handleLineId} />

          <button onClick={handleSubmit}>儲存變更</button>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
