import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useParams, useNavigate } from "react-router-dom";

const ProductSelf = () => {
  const navigate = useNavigate();
  const { _id } = useParams();
  const [productData, setProductData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    ProductService.getById(_id)
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, [_id]);

  const handleDelete = () => {
    ProductService.delete(_id)
      .then(() => {
        window.alert("商品已刪除");
        navigate("/profile");
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handleEdit = () => {
    navigate(`/product/edit/${_id}`);
  };

  return (
    <div>
      {isLoading ? (
        <div>Loading...</div> // 显示加载状态
      ) : (
        productData &&
        productData.foundProduct && (
          <div>
            {productData.foundProduct.imageUrl.map((url, index) => {
              return (
                <div key={index}>
                  <img
                    src={url}
                    alt="圖片"
                    style={{
                      width: "200px",
                      height: "auto",
                    }}
                  />
                </div>
              );
            })}
            <div>{productData.foundProduct.name}</div>
            <div>{productData.foundProduct.quantity}</div>
            <div>{productData.foundProduct.price}</div>
            <button onClick={handleEdit}>編輯</button>
            <button onClick={handleDelete}>刪除商品</button>
          </div>
        )
      )}
    </div>
  );
};

export default ProductSelf;
