import React, { useEffect, useState } from "react";
import ProductService from "../services/product-service";
import { useParams } from "react-router-dom";
import CartService from "../services/cart-service";
const ProductDetail = () => {
  let [productData, setProductData] = useState(null);
  let [quantity, setQuantity] = useState(1);
  console.log(productData);
  const { _id } = useParams();
  console.log(productData);
  useEffect(() => {
    ProductService.getById(_id)
      .then((data) => {
        setProductData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const hadelQuantity = (e) => {
    setQuantity(e.target.value);
  };
  const hadelCartAdd = (e, _id) => {
    e.preventDefault();
    if (productData && productData.foundProduct) {
      CartService.CartAdd(_id, quantity)
        .then(() => {
          window.alert("商品已新增至購物車");
        })
        .catch((e) => {
          console.log(e);
        });
    }
  };
  return (
    <div>
      {productData && productData.foundProduct && (
        <div>
          <div>{productData.foundProduct.name}</div>
          <div>{productData.foundProduct.price}</div>
          <label htmlFor="quantitu">數量 </label>
          <input type="number" onChange={hadelQuantity} value={quantity} />
          <button onClick={hadelCartAdd(productData.foundProduct._id)}>
            加入購物車
          </button>
          <button>直接購買</button>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
